import java.util.Arrays;
public class Fruits {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String fruits[]= new String[] {"apple","mango","banana","cherry"}; //declaration and initialization of an array
		Arrays.sort(fruits);//sort an array
		for(String x:fruits)
		{
			System.out.println(x);// print the name of fruits
			
		}
		

	}

}
